/* reset 默认css*/
*{box-sizing: border-box;margin: 0px;padding: 0px;}
*:before, *:after{box-sizing: border-box;}

a:hover, a:visited, a:link, a:active {
    text-decoration: none; color: xxx;
}

/* 清除列表样式 */
ul,ol{list-style: none;}
input,button{background:none;outline:none;border:0px;}

.clearfix:after{ 
    content:"";
    display:block;
    clear:both;
}
/* nav样式 */
html{height:100%;}
body{background: linear-gradient(180deg, #F5F7FA 10%, rgb(195, 207, 226) 100%);height:100%;}

/* 鼠标样式*/
div{cursor: default;}

.siteBody{padding:50px 40px 35px 40px;}
.wrapBox{
    margin-bottom: 20px;
    padding:8px 10px 8px 10px;
    background: #F5F7FA;
    border-radius: 15px;
    box-shadow: 0 0 0 rgba(0,0,0,.05), 0 1px 2px rgba(0,0,0,.05), 0 3px 6px rgba(249,103,103,.05);
}

.searchTopWapper{margin-left: 16px;}

.searchBox{margin-bottom: 30px;}
.searchTopWapper .current{color:#4C5057;font-size:8px;float:left;background: #FFFEFF;
    box-shadow: 5px 10px 0 0 #FFFEFF,-5px 10px 0 0 #FFFEFF;border-radius: 5px 5px 0 0;
    padding:2px 5px;position: relative;margin-right: 8px;
}

.searchValue{color:#4068A7;font-size:8px;float:left;background:#F5F7FA;
    /*box-shadow: 10px 20px 0 0#F5F7FA,-10px 20px 0 0 #F5F7FA;*/border-radius: 5px 5px 0 0;
    padding:2px 5px;position: relative;margin-right: 8px;
}
.searchTopWapper .current::before{ 
position:absolute;
/* content:"\200B";。其实之前的再谈清除浮动 里也已经提到，这个 U+200B 字符是一个“零宽度空白”，其本身就不可见，所以也就不需要在使用 visibility:hidden; 来隐藏内容。*/
content:"";
background-color:#F5F7FA;
width:12px;
height:12px;
border-radius: 0 0 5px 0;
left:-12px;
bottom:0px;
    }
.searchTopWapper .current::after{
position:absolute;
content:"";
background-color:#F5F7FA;
width:12px;
height:12px;
border-radius: 0 0 0 5px;
right:-12px;
bottom:0px;
}
.searchTopWapper .current .index1{color:#242ED7}
.searchTopWapper .current .index2{ 
    background: linear-gradient(to right, #4285f4 12%, #EA4335 35%,  #FBBC05 65%,#44A759 100%);
    background-clip: text;
    color: transparent;}
.searchTopWapper .current .index3{color:#51C332}
.searchTopWapper .current .index4{color:#0084ff}
.searchTopWapper .current .index5{color:#00A1D6}

.searchBox > input{background: #FFFEFF;border-radius: 30px;float:left;height:30px;width:80%;padding-left: 25px;font-size: 10px;}
.searchBox > button{white-space: nowrap;float:right;font-size:10px;height:30px;padding:4px 8px;background: #9FBCE9;border-radius: 50px;color:#FFFFFF;}


.wrapBoxContent{color:#4C5057;margin:2px 0px 5px 0px;font-size:10px;}

.linkListWapper{display:flex;flex-wrap:wrap;align-content: flex-start;margin-right: -10px;}
.link .remove{opacity: 1;}
.link .remove .re1,.link .remove .re2{
    position: absolute;  /*方便进行定位*/
    height: 8px;
    width: 2px;
    top: 4px;
    right: 9px; 
   background: rgb(132, 133, 134);}

   .link .remove .re1{
    transform: rotate(45deg);  /*进行旋转*/
  }
  .link .remove .re2{
    transform: rotate(-45deg);
  }

.link{ height:70px; background: rgba(179, 180, 182, 0.2);margin: 3px 4px 3px 0px;flex: 0 0 31%;border-radius: 4px;position: relative;}
/*这里使用flex的流式布局，margin是调出来的*/
.linkWrapper{display: flex;flex-direction: column;justify-content: center;align-items: center;padding:4px 0px 4px 0px;}
.linkLogo{height:45px;width: 45px;border-radius: 50%;
    background: linear-gradient(to top, #ff512f, #dd2476);
    opacity: 0.6;
    margin: 2px auto 2px auto;
    color:white;font-size: 30px;
    text-align: center;vertical-align: middle;
    /*为啥这里加了这个，就自己垂直居中了呢？？？？？*/
    display: flex;flex-direction: column;justify-content: center;align-items: center;
}
.linkName{margin-top: 3px;font-size: 8px;}


.addFav{ height:70px; background: rgba(179, 180, 182, 0.2);margin: 3px 4px 3px 0px;flex: 0 0 31%;border-radius: 4px;}
.addWrapper{display: flex;flex-direction: column;justify-content: center;align-items: center;}
.addLogo{
    height:50px; width: 50px; border-radius: 50%;
    background: linear-gradient(to top, #ff512f, #dd2476);
    opacity: 0.6;
    margin: 10px auto;
    text-align: center;vertical-align: middle;
    /*为啥这里加了这个，就自己垂直居中了呢？？？？？*/
    display: flex;flex-direction: column;justify-content: center;align-items: center;
}
.addLogo > .icon{
    height:35px; width: 35px;
    margin-top:1px;
    color:white;
}
.navTabsWarpper .current{color:#4068A7;border-bottom:2px solid #4068A7;}


.navTab{color:#4C5057;font-size: 8px;float:left;margin: 5px 5px 3px 0px;padding:0px 3px;} 
.navList{margin-top: 3px;}
/*这里使用flex布局*/
.navContent{margin-right:-10px; display:flex;flex-wrap:wrap;align-content: flex-start;}
.navContentBox{flex: 0 0 32%;;height:35px; background: rgba(179, 180, 182, 0.2);
    padding:2px 4px ;margin:0px 1px 3px 0px;
    overflow: hidden;
    border-radius: 4px;}
.navContent{display:none;}

.navContentBox .ln{
    margin:5px 0px 0px 0px;
    font-size:8px;
    color:#4068A7;}
.navContentBox .ld{
    margin-top: 2px;
    font-size:5px;
    color:#91959D;
}

