.navContentBox{width:130px;height:50px; background: yellow;float: left;margin:0px 10px 5px 0px;border-radius: 4px;}
*/
.navList{margin-top: 2px;}
.navContent{margin-right:-10px; display:flex;flex-wrap:wrap;align-content: flex-start;}
.navContent{display:none;}

.navContentBox{flex: 0 0 16%;;height:50px; background: #F5F7FA;
    box-shadow: 0 0 0 rgba(0,0,0,.05), 0 1px 2px rgba(0, 0, 0, 0.05), 0 1px 4px rgba(0,0,0,.05);
    margin:0px 3px 3px 0px;border-radius: 4px;
    padding:8px 6px;overflow: hidden;}
.navContentBox:hover{transform: scale(1.08);
    background-color:rgba(245, 246, 248, 0.945);
    box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.15);}
.navContentBox .ln{
    margin:20px 0px 10px 0px;
    font-size:12px;
    color:#4068A7;}
.navContentBox .ld{
    font-size:7px;
    color:#91959D;
}
.navContentBox:hover .ln,.navContentBox:hover .ld{
    background: linear-gradient(to right, #ff512f, #dd2476);
    -webkit-background-clip: text;
    background-clip: text;
    color: transparent;
}

